  {{# repeat (queryParam 'limit' '10') }}
    {
      "uid": "{{ faker 'datatype.number' min=10000 max=100000 }}",
      "username": "{{ faker 'internet.userName' }}",
      "description": "{{ faker 'lorem.paragraph' 10 }}",
      "email": "{{ faker 'internet.email'  }}",
      "avatar": "{{ faker 'internet.avatar' }}"
    }
  {{/repeat}}
]

[
  {{# repeat (queryParam 'limit' '10') }}
    {
      "id": "bk{{ faker 'datatype.number' min=0 max=1000 }}",
      "title": "{{ faker 'word.noun' 20 }}",
      "cover": "{{ faker 'image.image' 1280 1910 }}",
      "tags": {{{ someOf (array 'novel' 'essays' 'poetry' 'short-stories' 'fairy-tale' 'comics' 'reasoning' 'picture-book' 'science-fiction' 'youth-romance' 'fantasy' 'martial-arts' 'history' 'philosophy' 'biography' 'design' 'film' 'architecture' 'memoir' 'music') 1 5 true }}},
      "rates": {{ faker 'datatype.number' 100 }},
      "mate": {
        "author": "{{ faker 'name.firstName' }} {{ faker 'name.lastName' }}"
      },
      "desc": {
        "short_desc": "{{ faker 'lorem.paragraph' 10 }}"
      }
    }
  {{/repeat}}
]

{
  "id": "{{urlParam 'bkid'}}",
  "title": "{{ faker 'word.noun' 20 }}",
  "cover": "{{ faker 'image.image' 1280 1910 }}",
  "tags": {{{ someOf (array 'novel' 'essays' 'poetry' 'short-stories' 'fairy-tale' 'comics' 'reasoning' 'picture-book' 'science-fiction' 'youth-romance' 'fantasy' 'martial-arts' 'history' 'philosophy' 'biography' 'design' 'film' 'architecture' 'memoir' 'music') 1 5 true }}},
  "rates": {{ faker 'datatype.number' 100 }},
  "mate": {
    "author": "{{ faker 'name.firstName' }} {{ faker 'name.lastName' }}",
    "publisher": "{{ faker 'company.companyName' }}",
    "subtitle": "{{ faker 'lorem.sentence' 20 }}",
    "language": "{{ oneOf (array 'Afrikaans' 'Arabic' 'Azerbaijani' 'Czech' 'German' 'Greek' 'English' 'Spanish' 'Farsi' 'Finnish' 'French' 'Georgian' 'Hebrew' 'Hrvatski' 'Hungarian' 'Armenian' 'Indonesia' 'Italian' 'Japanese' 'Korean' 'Latvian' 'Macedonian' 'Norwegian' 'Nepalese' 'Dutch' 'Polish' 'Romanian' 'Russian' 'Slovakian' 'Swedish' 'Turkish' 'Ukrainian' 'Urdu' 'Vietnamese' 'Chinese') }}",
    "{{ oneOf (array 'paperback' 'hardcover') }}": {{ faker 'datatype.number' min=100 max=10000 }},
    "publication_date": "{{ faker 'date.past' }}",
    "isbn": "978-{{ faker 'datatype.number' min=1000000000 max=1999999999 }}"
  },
  "desc": {
    "text": "{{ faker 'lorem.paragraph' 10 }}",
    "is_folded": {{boolean}}
  },
  "digest": {
    "text": "{{ faker 'lorem.paragraph' 10 }}",
    "is_folded": {{boolean}}
  }
}

[
  {{# repeat (queryParam 'limit' '20') }}
    {
      "id": "cm{{ faker 'datatype.number' min=1000000000 max=100000000000 }}",
      "author_meta": {
        {{# if (queryParamRaw 'uid') }}
        "uid": "{{queryParam 'uid' }}",
        {{else}}
        "uid": "{{ faker 'datatype.number' min=10000 max=100000 }}"
        {{/if}}
        "username": "{{ faker 'internet.userName' }}",
        "email": "{{ faker 'internet.email'  }}",
        "avatar": "{{ faker 'internet.avatar' }}"
      },
      "meta": {
        "short_review": "{{ faker 'lorem.lines' 1 }}",
        "rates": {{ faker 'datatype.number' 100 }},
        "like": {{ faker 'datatype.number' 1000 }},
        "dislike": {{ faker 'datatype.number' 1000 }},
        "comment_date": "{{ faker 'date.past' 10 }}",
        "location": "{{ faker 'address.county' }}"
      },
      "respond": [
        {{# repeat 0 20 }}
        {
          "id": "cm{{ faker 'datatype.number' min=1000000000 max=100000000000 }}",
          "author_meta": {
            "uid": "{{ faker 'datatype.number' min=10000 max=100000 }}",
            "username": "{{ faker 'internet.userName' }}",
            "email": "{{ faker 'internet.email'  }}",
            "avatar": "{{ faker 'internet.avatar' }}"
          },
          "meta": {
            "like": {{ faker 'datatype.number' 1000 }},
            "dislike": {{ faker 'datatype.number' 1000 }},
            "comment_date": "{{ faker 'date.past' 10 }}",
            "location": "{{ faker 'address.county' }}"
          },
          "comment_date": "{{ faker 'date.past' 10 }}",
          "belongs_to": "cm{{ faker 'datatype.number' min=1000000000 max=100000000000 }}",
          "msg": "{{ faker 'lorem.lines' 1 }}",
          "is_folded": {{boolean}}
        }
        {{/repeat}}
      ],
      {{# if (queryParamRaw 'bkid') }}
      "belongs_to": "{{queryParam 'bkid' }}",
      {{else}}
      "belongs_to": "bk{{ faker 'datatype.number' min=0 max=1000 }}",
      {{/if}}
      "msg": "{{ faker 'lorem.paragraph' 6 }}",
      "is_folded": {{boolean}}
    }
  {{/repeat}}
]

